mapper.select:

SELECT user_id, user_pw, user_nickname, user_email, user_receive_adv, user_reg_date
FROM ojakgyo.user_info
WHERE user_id = #{userId}


SELECT user_id, user_pw, user_nickname, user_email, user_receive_adv, user_reg_date
FROM ojakgyo.user_info
WHERE user_email = #{email}


SELECT COUNT(*)
FROM ojakgyo.user_info
WHERE user_id = #{userId}


SELECT COUNT(*)
FROM ojakgyo.user_info
WHERE user_email = #{email}

mapper.update:

UPDATE ojakgyo.user_info
SET user_pw = #{password}, user_nickname = #{nickname}, user_email = #{email}, user_receive_adv = #{receiveAd}
WHERE user_id = #{userId}
